void Map::openMap()
{
	int length = 17;
	int selectNum = 0;
	int input;
	int inputArrow;

	while (true)
	{
		Display::image(getPositionMap(selectNum));
		printPositionMap(selectNum);

		input = _getch();
		if (input == 224)
		{
			inputArrow = _getch();
			switch (inputArrow)
			{
			case 72: case 77:
				selectNum++;
				if (selectNum > length) selectNum = 0;
				break;
			case 75: case 80:
				selectNum--;
				if (selectNum < 0) selectNum = length;
				break;
			}
		}
		else if (input == 13)
		{
			break;
		}
		else
		{
			continue;
		}
	}
	
	if (selectNum == 0)
	{
		return;
	}
	else
	{
		system("cls");
		Dungeon* d = Container::getInstance()->getDungeon();
		d->dungeon(selectNum - 1);
	}
}

